import React, { useState, useEffect } from "react";
import Navbar from "../Header/Navbar";
import Footer from "../Footer/Footer";
import { Card } from "react-bootstrap";
import "./Home.css"
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css"
import axios from "axios";
const Home = () => {
    const [apidata,setapidata]=useState([]);
    
    const [config,setconfig]=useState(null);
    useEffect(()=>{
        setconfig({
            headers: {
            Authorization: localStorage.getItem('token')
        }
       })
       
    },[])
    if(config!==null){
    axios.get('https://food-app-timesinternet.herokuapp.com/api/staff/restaurant',config).then(res=>{
            console.log(res); 
            setapidata(res.data.restaurantDetail);
         }).catch(console.error())

        }   

    return (
        <>
            <Navbar />

            <div className="d-flex align-items-center">
                <div className="container">
                    <div className="row workingdiv outline mx-auto">
                        <div className="row">
                            <div className="col-4 order-2 order-lg-1 header-img">
                                <img src="https://source.unsplash.com/300x300/?nature,water" alt="sorry" className="m-3" />
                            </div>

                            <div className="col-8 pt-5 pt-lg-0 order-1 order-lg-2 resdetail">
                                <h1>Sarma Sweet's</h1>
                                <h3>{apidata?.address?.line1}</h3>
                                <h3>{apidata?.address?.line2}</h3>
                                <h3>{apidata?.address?.city} {apidata?.address?.state} {apidata?.address?.pincode} </h3>
                                <h3>Open at: {apidata?.address?.openingTime}</h3>
                                <h3>Closes at: {apidata?.address?.closingTime}</h3>
                                <h3>Status: {apidata?.address?.status}</h3>
                            </div>
                        </div>
                    </div>

                </div>
            </div>





            <Footer />
        </>
    )



}
export default Home;
