import React,{useState} from "react";
import { Form, Button } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import "./Stafflogin.css";
import axios from "axios";

const Stafflogin = () => {
    const [val,setval]=useState({
       email:"",
       password:""
    });
    const [fval,setfval]=useState({
       email:"",
       password:""
    });


    const func=(e)=>{
         const {name,value}=e.target;
         setval((prevval)=>{
             return{
                 ...prevval,
                 [name]:value
             }
         })
    }
    const func1=(e)=>{
         e.preventDefault();
         
         setfval({
             email:val.email,
             password:val.password
        })
        const url="https://food-app-timesinternet.herokuapp.com/api/staff/login"
        axios.post(url,fval)
        .then((res)=>{
             console.log(res.data.token);
             localStorage.setItem('token',res.data.token);
        })
        .catch(console.error());

        axios.get('https://food-app-timesinternet.herokuapp.com/api/staff/category').then(res=>{
           console.log(res); 
           
        }).catch(console.error())

    }
    return (
        <>
            <div className="container-fluid ">
                <div className="row">
                    <div className="col-8 mx-auto login_form">
                        <h3 className="staffloginheader">Staff-Login</h3>
                        <Form onSubmit={func1}>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>Email address</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" 
                                   onChange={func}
                                   value={val.email}
                                   name="email"
                                />
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" 
                                   onChange={func}
                                   value={val.password}
                                   name="password"
                                />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formBasicCheckbox">
                                <Form.Check type="checkbox" label="Remember Me" />
                            </Form.Group>
                            <Button variant="primary" type="submit">
                                Submit
                            </Button>
                        </Form>


                    </div>
                </div>
            </div>

        </>
    )
}
export default Stafflogin;
