        password: password
   })
      const url="https://food-app-timesinternet.herokuapp.com/api/staff/login"
        axios.post(url,fval)
        .then((res)=>{
             console.log(res.data.token);
             localStorage.setItem('token',res.data.token);
        })
        .catch(console.error());
      setToken(bearerToken)
    })
  }

  // function signOut() {
