                                <h2 className="card_title">{info.name}</h2>
                                <p className="card_text">Category: Indian</p>
                                <button className="btn1 card_btn">â‚¹ {info.price}/-</button>
                            </div>
                        </div>
                    </li>
            </>
        )
    }
    return (
        <>
            <div className="main123">
                <h1>Menu Items</h1>
                <ul className="cards123">
                   {arr.map(rendercards)}
                    
                </ul>
            </div>
        </>
    )
}
export default MenuItemCards
