import React, { useState, useEffect } from "react";
import Navbar from "../Header/Navbar";
import Footer from "../Footer/Footer";
import CartMain from "../Cart/CartMain"
import { Card } from "react-bootstrap";
import "./Home.css"
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css"
import axios from "axios";
import LocationOnIcon from '@material-ui/icons/LocationOn';
import { Redirect } from "react-router-dom";
import e from "express";
const Home = () => {
    const [apidata, setapidata] = useState([]);

    const [config, setconfig] = useState(null);
    // useEffect((e) => {
    //     e.preventDefaults();
    //     setconfig({
    //         headers: {
    //             Authorization: localStorage.getItem('token')
    //         }
    //     })

    // }, [config])
    // if (config !== null) {
    //     axios.get('https://food-app-timesinternet.herokuapp.com/api/staff/restaurant', config).then(res => {
    //         console.log(res);
    //         setapidata(res.data.restaurantDetail);
    //     }).catch(console.error())

    // }

    const [redirectmenu, setredirectmenu] = useState(0);
    const tomenu=(e)=>{
        e.preventDefault();
        setredirectmenu(1);
    }
    if(redirectmenu){
        <Redirect to="/menu" />
    }

    return (
        <>
            <Navbar />

            <div className="d-flex align-items-center">
                <div className="container">
                    <div className="row workingdiv outline mx-auto">
                        <div className="row">
                            <div className="col-4 order-2 order-lg-1 header-img">
                                <img src="https://source.unsplash.com/1000x1000/?nature,water" alt="sorry" className="m-3 img-fluid" />
                            </div>

                            <div className="col-8 pt-5 pt-lg-0 order-1 order-lg-2 ">
                                <h1 className="text-lg-center text-success">{apidata?.name}</h1>
                                <div >
                                    <div className="addressdetail">
                                        <LocationOnIcon/>
                                        {apidata?.address?.line1} , 
                                         {apidata?.address?.line2} <br />
                                        {apidata?.address?.city} {apidata?.address?.state} {apidata?.address?.pincode}
                                    </div>
                                    <div className=" h4 p-5 ">
                                        Open at: {apidata?.openingTime}<br/>
                                        Closes at: {apidata?.closingTime}<br/>
                                        Status: <strong>{apidata?.status}</strong>
                                    </div>

                                </div>
                            </div>
                        </div>
                    </div>

                    

                    <div className="buttons d-flex align-items-center">

                         <button type="button" className="btn-lg btn-outline-dark" onClick={tomenu}>Add Item</button>
                         <button type="button" className="btn-lg btn-outline-dark">Add Location</button>
                         <button type="button" className="btn-lg btn-outline-dark">Add Coupon</button>
                         <button type="button" className="btn-lg btn-outline-dark">Add Staff</button>
                    </div>




                </div>
            </div>


            <CartMain />


            <Footer />
        </>
    )



}
export default Home;
