import { useState } from "react"
import { Form, Button } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

function AddCoupon(props) {

    const [formValues, setFormValues] = useState({
        title: '',
        value: '',
        imgURL: '',
    })

    function addCoupon(event) {
        event.preventDefault(); // prevents refreshing
        const postCoupon={
            formValues
        }
        axios.post('http://localhost:3000/PincodeList', postCoupon ).then((response)=>{
            console.log(response);
        })
        alert("Submitted!" + " title: " + formValues.title + " value: " + formValues.value + formValues.imgURL);
        setFormValues({
            title: '',
            value: '',
            imgURL: '',
        })
    }

    function handleInputChange(event) {
        setFormValues((prevState) => {
            return {
                ...prevState,
                [event.target.name]: event.target.value
            }
        })
    }

    function handleFileChange(event) {
        setFormValues((prevState) => {
            return {
                ...prevState,
                [event.target.name]: event.target.files[0].name
            }
        })
    }

    return (
        <div style={{width: "400px", margin: "auto"}}>
            <Form onSubmit={addCoupon}>
                <h3>Add Coupon</h3>

                <Form.Group className="mb-3" controlId="title">
                    <Form.Label>Coupon Name</Form.Label>
                    <Form.Control type="text" placeholder="Coupon Name..." name="title" onChange={handleInputChange} value={formValues.title} />
                </Form.Group>

                <Form.Group controlId="formFile" className="mb-3">
                    <Form.Label>Add Coupon Image</Form.Label>
                    <Form.Control type="file" onChange={handleFileChange} name="imgURL" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="value">
                    <Form.Label>Coupon Vaule</Form.Label>
                    <Form.Control type="text" placeholder="Value..." name="value" onChange={handleInputChange} value={formValues.value} />
                </Form.Group>

                <Button variant="primary" type="submit" style={{ float: 'right' }} >
                    Add Coupon
                </Button>
            </Form>

            <img src={formValues.imgURL} alt="" />
        </div>
    )
}

export default AddCoupon
